export default function getJsxPreview(code) {
  /* The regex matches the content of the return statement in the default export,
   * stripping any wrapper divs:
   *
   * `export default.*`
   * `\n  return (\n` or `\n  return `
   * `    <div.*>\n` (optional)
   *  everything until:
   * `\n    </div>` (optional)
   * `  );\n}` or `;\n}`
   */
  var jsx = code.match(/export default [\s\S]*(?:\n {2}return \(\n|\n {2}return )(?: {4}<div[\s\S]*?>\n)?([\s\S]*?)(\n {4}<\/div>)?(\n {2}\);\n\}|;\n\})/); // Just the match, otherwise the full source if either no match or preview disabled,
  // so as not to break the Collapse transition.

  jsx = jsx ? jsx[1] : code; // Remove leading spaces from each line

  return jsx.split(/\n/).reduce(function (acc, line) {
    return "".concat(acc).concat(line.slice( // Number of leading spaces on the first line
    jsx.match(/^ */)[0].length), "\n");
  }, '');
}
